use light_bitcoin::{
    primitives::H256,
    script::{Builder, Opcode, Script},
};

fn reverse_h256(mut hash: H256) -> H256 {
    let bytes = hash.as_bytes_mut();
    bytes.reverse();
    H256::from_slice(bytes)
}

fn as_h256(s: &str) -> H256 {
    h256_conv_endian_from_str(s)
}

